let T = msg.payload.temp1;
let RH = msg.payload.rh;

if (T == null || RH == null) {
    return null;  // abbrechen, wenn ein Wert fehlt
}

let svp = 0.6108 * Math.exp((17.27 * T) / (T + 237.3));
let avp = svp * (RH / 100);
let vpd = svp - avp;

// Für Gauge oder Chart: Nur den VPD-Wert zurückgeben
let vpd_only = parseFloat(vpd.toFixed(2));

let msg_gauge = { payload: vpd_only };        // für Chart oder Gauge
let msg_full = {
    payload: {
        temperature: T,
        humidity: RH,
        vpd: vpd_only
    }
};  // für Debug oder OLED

return [msg_gauge, msg_full];

flow.set("rh", msg.payload);     // Wert speichern
let temp1 = flow.get("temp1");   // Temp abrufen

if (temp1 !== undefined) {
    return { payload: { temp1: temp1, rh: msg.payload } };
}
return null;  // Temperatur noch nicht da

flow.set("temp1", msg.payload);  // Wert speichern
let rh = flow.get("rh");         // RLF abrufen

if (rh !== undefined) {
    return { payload: { temp1: msg.payload, rh: rh } };
}
return null;  // RLF noch nicht da
