// Werte laden
const istAktiv = flow.get("licht_aktiv") ?? true;
const modusSA_SU = flow.get("licht_sonnenaufgang_aktiv") ?? false;
const manuell = flow.get("licht_modus") ?? true;
const start = flow.get("licht_start") ?? "00:00";
const ende = flow.get("licht_ende") ?? "23:59";
const sunrise = parseInt(flow.get("licht_sonnenaufgang") ?? 0, 10);
const helligkeit_auto = flow.get("licht_helligkeit_auto") ?? 100;
const helligkeit_manuell = flow.get("licht_helligkeit_manuell") ?? 100;

// Debug: Eingangs- und Flowwerte
node.warn({
    istAktiv, modusSA_SU, manuell, start, ende, sunrise, helligkeit_auto, helligkeit_manuell
});

// Uhrzeit berechnen
const jetzt = new Date();
const jetztMin = jetzt.getHours() * 60 + jetzt.getMinutes();
const startSplit = start.split(":");
const endeSplit = ende.split(":");
const startMin = parseInt(startSplit[0], 10) * 60 + parseInt(startSplit[1], 10);
const endeMin = parseInt(endeSplit[0], 10) * 60 + parseInt(endeSplit[1], 10);

// Debug: Zeitwerte
node.warn({ jetztMin, startMin, endeMin });

// Licht aktiv während Zeitfenster?
let lichtAn = istAktiv && (jetztMin >= startMin && jetztMin <= endeMin);
node.warn({ lichtAn });

// Zielhelligkeit: je nach Modus
let zielwert = manuell ? helligkeit_manuell : helligkeit_auto;
node.warn({ zielwert_vor_SA_SU: zielwert });

// Sonnenaufgang/-untergang (wenn aktiv & Auto)
if (lichtAn && !manuell && modusSA_SU && sunrise > 0) {
    const t = jetztMin - startMin;
    const gesamt = endeMin - startMin;
    node.warn({ t, gesamt });
    if (t < sunrise) {
        zielwert = Math.round(zielwert * (t / sunrise));
        node.warn({ phase: 'Sonnenaufgang', zielwert });
    } else if (t > gesamt - sunrise) {
        zielwert = Math.round(zielwert * ((gesamt - t) / sunrise));
        node.warn({ phase: 'Sonnenuntergang', zielwert });
    }
}

// Wenn außerhalb Lichtzeit: aus
if (!lichtAn) {
    zielwert = 0;
    node.warn({ phase: 'außerhalb Zeit', zielwert });
}

// Begrenzen (Sicherheitsgrenze)
zielwert = Math.max(0, Math.min(100, zielwert));
node.warn({ zielwert_final: zielwert });

// Anzeige im Node
node.status({
    fill: lichtAn ? "green" : "grey",
    shape: "dot",
    text: (manuell ? "Manuell: " : "Auto: ") + zielwert + "%"
});

// Ausgabe für Anzeige (Text mit Prozent)
let anzeige = zielwert + " %";

// Ausgabe für PWM (Skala 0–255)
let pwm = Math.round(zielwert * 2.55);
node.warn({ anzeige, pwm });

// Zwei Ausgänge:
return [
    { payload: anzeige },  // → ui-text für Prozentanzeige
    { payload: pwm }       // → PWM-Ausgang
];

let modus = msg.payload;
if (typeof modus === "object" && modus !== null) {
    if (typeof modus.value !== "undefined") modus = modus.value;
}
if (modus === true || modus === "true" || modus === 1 || modus === "1") {
    flow.set("licht_modus", true);
    node.status({ fill: "green", shape: "dot", text: "Manuell" });
} else if (modus === false || modus === "false" || modus === 0 || modus === "0") {
    flow.set("licht_modus", false);
    node.status({ fill: "grey", shape: "dot", text: "Automatik" });
}
return null;

// ...bestehender Code...
let wert = msg.payload;
if (typeof wert === 'object' && wert !== null) {
    if (typeof wert.value !== 'undefined') wert = wert.value;
}
wert = Number(wert);
if (!isNaN(wert)) {
    flow.set("helligkeit_manuell", wert);
    node.warn("Helligkeit manuell gespeichert: " + wert);
}
return null;
// ...bestehender Code...

// ...bestehender Code...
let wert = msg.payload;
if (typeof wert === 'object' && wert !== null) {
    if (typeof wert.value !== 'undefined') wert = wert.value;
}
wert = Number(wert);
if (!isNaN(wert)) {
    flow.set("licht_helligkeit_auto", wert);
    node.warn("Helligkeit auto gespeichert: " + wert);
}
return null;
// ...bestehender Code...

let sa_su = msg.payload;
if (typeof sa_su === "object" && sa_su !== null) {
    if (typeof sa_su.value !== "undefined") sa_su = sa_su.value;
}
if (sa_su === true || sa_su === "true" || sa_su === 1 || sa_su === "1") {
    flow.set("licht_sonnenaufgang_aktiv", true);
    node.status({ fill: "blue", shape: "dot", text: "SA/SU aktiv" });
} else if (sa_su === false || sa_su === "false" || sa_su === 0 || sa_su === "0") {
    flow.set("licht_sonnenaufgang_aktiv", false);
    node.status({ fill: "grey", shape: "dot", text: "SA/SU aus" });
}
return null;

// Vollständiges Debug
node.warn({
    payload: msg.payload,
    payload_typ: typeof msg.payload,
    attributes: msg.data?.attributes
});

const attr = msg.data?.attributes;
let zeit;

// Fall 1: Datum + Zeit → timestamp vorhanden
if (typeof attr?.timestamp === "number") {
    const d = new Date(attr.timestamp * 1000);
    const hh = String(d.getHours()).padStart(2, "0");
    const mm = String(d.getMinutes()).padStart(2, "0");
    zeit = `${hh}:${mm}`;
}

// Fall 2: Nur Zeit → hour/minute
else if (attr?.has_time && typeof attr.hour === "number") {
    const hh = String(attr.hour).padStart(2, "0");
    const mm = String(attr.minute ?? 0).padStart(2, "0");
    zeit = `${hh}:${mm}`;
}

// Ungültig
else {
    node.warn({
        status: "FEHLER",
        grund: "Keine Zeitinformationen gefunden",
        attributes: attr
    });
    return null;
}

// Speichern
flow.set("licht_start", zeit);

node.status({
    fill: "green",
    shape: "dot",
    text: "Startzeit: " + zeit
});

node.warn({
    status: "OK",
    gespeichert: zeit
});

return null;

if (typeof msg.payload === "string" && msg.payload.includes(":")) {
    flow.set("licht_ende", msg.payload);
    node.warn("Endzeit gespeichert: " + msg.payload);
}
return null;

// Sonnenaufgangszeit speichern
let zeit = msg.payload;
if (typeof zeit === "object" && zeit !== null) {
    if (typeof zeit.time !== "undefined") zeit = zeit.time;
    else if (typeof zeit.value !== "undefined") zeit = zeit.value;
}
if (typeof zeit === "number") {
    // z.B. Minuten seit Mitternacht in Zahl
    let h = Math.floor(zeit / 60).toString().padStart(2, '0');
    let m = (zeit % 60).toString().padStart(2, '0');
    zeit = h + ':' + m;
}
flow.set("licht_sonnenaufgang", zeit);
return null;

// ---------- Entität bestimmen ----------
const entityId =
    msg.data?.entity_id ||
    msg.data?.entityId ||
    msg.data?.entity_id;

if (!entityId) {
    node.warn("Keine entity_id gefunden");
    return null;
}

// ---------- Mapping ----------
let flowKey;
let label;
let value;

// ---------- input_boolean ----------
if (entityId.startsWith("input_boolean.")) {
    value = msg.payload === "on";

    switch (entityId) {
        case "input_boolean.manuell":
            flowKey = "licht_modus";
            label = "Manuell";
            break;

        case "input_boolean.sa_su_automatik":
            flowKey = "licht_sonnenaufgang_aktiv";
            label = "SA/SU";
            break;

        case "input_boolean.zeitschaltuhr":
            flowKey = "licht_aktiv";
            label = "Zeitschaltuhr";
            break;
    }
}

// ---------- input_number ----------
else if (entityId.startsWith("input_number.")) {
    value = Number(msg.payload);

    switch (entityId) {
        case "input_number.helligkeit_manuell":
            flowKey = "licht_helligkeit_manuell";
            label = "Helligkeit Manuell";
            break;

        case "input_number.lichtwert_zeitschaltuhr":
            flowKey = "licht_helligkeit_auto";
            label = "Helligkeit Auto";
            break;
    }
}

// ---------- input_datetime ----------
else if (entityId.startsWith("input_datetime.")) {
    const ts = msg.data?.attributes?.timestamp;
    if (!ts) {
        node.warn("Kein Timestamp für " + entityId);
        return null;
    }

    const d = new Date(ts * 1000);
    const hh = String(d.getHours()).padStart(2, "0");
    const mm = String(d.getMinutes()).padStart(2, "0");
    value = `${hh}:${mm}`;

    switch (entityId) {
        case "input_datetime.einschaltzeit":
            flowKey = "licht_start";
            label = "Startzeit";
            break;

        case "input_datetime.ausschaltzeit":
            flowKey = "licht_ende";
            label = "Endzeit";
            break;
    }
}

// ---------- Unbekannt ----------
if (!flowKey) {
    node.warn("Nicht gemappte Entität: " + entityId);
    return null;
}

// ---------- Wert speichern ----------
flow.set(flowKey, value);

// ---------- Status ----------
node.status({
    fill: "green",
    shape: "dot",
    text: `${label}: ${value}`
});

// ---------- Debug ----------
node.debug({
    entity: entityId,
    flowKey,
    value
});

return null;
