const sunrise = parseInt(flow.get("licht_sonnenaufgang") ?? 0);
const helligkeit_auto = flow.get("licht_helligkeit_auto") ?? 100;
const helligkeit_manuell = flow.get("licht_helligkeit_manuell") ?? 100;

// Uhrzeit berechnen
const jetzt = new Date();
const jetztMin = jetzt.getHours() * 60 + jetzt.getMinutes();
const startMin = parseInt(start.split(":")[0]) * 60 + parseInt(start.split(":")[1]);
const endeMin = parseInt(ende.split(":")[0]) * 60 + parseInt(ende.split(":")[1]);

// Licht aktiv w√§hrend Zeitfenster?
let lichtAn = istAktiv && (jetztMin >= startMin && jetztMin <= endeMin);

// Zielhelligkeit: je nach Modus
let zielwert = manuell ? helligkeit_manuell : helligkeit_auto;

// Sonnenaufgang/-untergang (wenn aktiv & Auto)
if (lichtAn && !manuell) {
    if (modusSA_SU) {
        const t = jetztMin - startMin;
        const gesamt = endeMin - startMin;

        if (t < sunrise) {
            zielwert = Math.round(zielwert * (t / sunrise));
        } else if (t > gesamt - sunrise) {
            zielwert = Math.round(zielwert * ((gesamt - t) / sunrise));
        }
    }
}

// Wenn au√üerhalb Lichtzeit: aus
if (!lichtAn) zielwert = 0;

// Begrenzen (Sicherheitsgrenze)
zielwert = Math.max(0, Math.min(100, zielwert));

