let statusText = knoten.map(k => {
    let last = flow.get(k + "_lastSeen") || 0;
    let diff = (now - last) / 1000;
    let online = diff < timeout;
    let emoji = online ? "üü¢" : "üî¥";
    let state = online ? "Online" : "Offline";
    return `${emoji} ${k}: ${state}`;
}).join("<br>");

msg.payload = statusText;
msg.html = true;  // wichtig f√ºr das Dashboard!
return msg;

